package com.ricoh.paypal.pojo.product;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.Data;/** * @author xionglijing * @date 2021/10/11 16:23 * @description */@Datapublic class Product{    /**     * 商品名称     */    private String name;    /**     * 商品描述     */    private String description;    /**     *产品类型。     * PHYSICAL.    实物商品     * DIGITAL.     数码产品     * SERVICE.      一项服务。例如，技术支持     */    private String type;    /**     * 产品类别。     * BOOKS_PERIODICALS_AND_NEWSPAPERS.  书籍、期刊及报章     */    private String category;    /**     * 图片链接     */    @JsonProperty(value = "image_url")    private String imageUrl;    /**     * 商品首页     */    @JsonProperty(value = "home_url")    private String homeUrl;}